import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

// --- URL base da sua API (JSON Server) ---
const API_BASE_URL = 'http://localhost:3001';

// --- Componente principal da aplicação ---
function App() {
  // Estados para gerenciamento de navegação
  // Removida a necessidade de isLoggedIn, pois não haverá login inicial
  const [currentPage, setCurrentPage] = useState('dashboard'); // Define o dashboard como a página inicial

  // Estados para gerenciamento de beneficiários e formulário
  const [beneficiarios, setBeneficiarios] = useState([]);
  const [formData, setFormData] = useState({
    nome: '',
    idade: '',
    email: '',
    telefone: '',
    tipoDeAjuda: '',
    situacaoSocial: ''
  });
  const [editingId, setEditingId] = useState(null);
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [beneficiaryToDeleteId, setBeneficiaryToDeleteId] = useState(null);

  // Efeito para carregar os beneficiários ao montar o componente ou quando a página muda
  // Agora, os beneficiários são carregados sempre que a página for dashboard ou listBeneficiaries
  useEffect(() => {
    if (currentPage === 'dashboard' || currentPage === 'listBeneficiaries') {
      fetchBeneficiarios();
    }
  }, [currentPage]); // Dependência: currentPage

  // Função para buscar os beneficiários da API
  const fetchBeneficiarios = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/beneficiarios`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setBeneficiarios(data);
    } catch (error) {
      console.error("Erro ao buscar beneficiários:", error);
    }
  };

  // Lida com a mudança nos campos do formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Lida com o envio do formulário (cadastro ou edição)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      // Atualizar beneficiário existente
      try {
        const response = await fetch(`${API_BASE_URL}/beneficiarios/${editingId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        setBeneficiarios(beneficiarios.map(b => (b.id === editingId ? { ...formData, id: editingId } : b)));
        resetForm();
      } catch (error) {
        console.error("Erro ao atualizar beneficiário:", error);
      }
    } else {
      // Cadastrar novo beneficiário
      try {
        const response = await fetch(`${API_BASE_URL}/beneficiarios`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const newBeneficiary = await response.json();
        setBeneficiarios([...beneficiarios, newBeneficiary]);
        resetForm();
      } catch (error) {
        console.error("Erro ao cadastrar beneficiário:", error);
      }
    }
  };

  // Preenche o formulário com os dados do beneficiário para edição
  const handleEdit = (beneficiary) => {
    setFormData(beneficiary);
    setEditingId(beneficiary.id);
    setCurrentPage('listBeneficiaries'); // Garante que o formulário esteja visível
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Confirmação antes de excluir
  const confirmDelete = (id) => {
    setBeneficiaryToDeleteId(id);
    setShowConfirmModal(true);
  };

  // Lida com a exclusão de um beneficiário
  const handleDelete = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/beneficiarios/${beneficiaryToDeleteId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      setBeneficiarios(beneficiarios.filter(b => b.id !== beneficiaryToDeleteId));
      setShowConfirmModal(false);
      setBeneficiaryToDeleteId(null);
    } catch (error) {
      console.error("Erro ao excluir beneficiário:", error);
      setShowConfirmModal(false);
      setBeneficiaryToDeleteId(null);
    }
  };

  // Reseta o formulário e o estado de edição
  const resetForm = () => {
    setFormData({
      nome: '',
      idade: '',
      email: '',
      telefone: '',
      tipoDeAjuda: '',
      situacaoSocial: ''
    });
    setEditingId(null);
  };

  // A função renderPage foi simplificada para não ter lógica de login/registro
  const renderPage = () => {
    switch (currentPage) {
      case 'dashboard':
        // Garante que beneficiarios é um array ao ser passado
        return <DashboardScreen beneficiaries={beneficiarios} onNavigate={setCurrentPage} />;
      case 'listBeneficiaries':
        return (
          <BeneficiaryManagementScreen
            beneficiarios={beneficiarios} // Garante que beneficiarios é um array ao ser passado
            formData={formData}
            editingId={editingId}
            handleChange={handleChange}
            handleSubmit={handleSubmit}
            handleEdit={handleEdit}
            confirmDelete={confirmDelete}
            resetForm={resetForm}
            onNavigate={setCurrentPage}
          />
        );
      default: // Fallback, caso currentPage seja algo inesperado
        // Garante que beneficiarios é um array ao ser passado
        return <DashboardScreen beneficiaries={beneficiarios} onNavigate={setCurrentPage} />;
    }
  };

  // A função de logout foi removida daqui, pois não há login para fazer logout
  // No entanto, se precisar de um botão "Sair" que apenas volte ao dashboard
  // você pode reintroduzir um botão simples no DashboardScreen ou em outro lugar.

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center py-8 px-4 sm:px-6 lg:px-8 font-inter">
      {renderPage()}

      {/* Modal de Confirmação de Exclusão - Mantido aqui para ser global */}
      {showConfirmModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-75 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-sm">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Confirmar Exclusão</h3>
            <p className="text-gray-700 mb-6">Tem certeza que deseja excluir este beneficiário? Esta ação não pode ser desfeita.</p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={() => setShowConfirmModal(false)}
                className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 transition ease-in-out duration-150"
              >
                Cancelar
              </button>
              <button
                onClick={handleDelete}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition ease-in-out duration-150"
              >
                Excluir
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

